mapper.select:
SELECT *
FROM `member` AS M
WHERE M.id = #{id}

SELECT *
FROM `member`
AS M
WHERE M.loginId = #{loginId}
and delStatus = 0;

SELECT *
FROM
`member` AS M
WHERE M.name = #{name}
AND M.email = #{email}
and delStatus = 0;

SELECT *
FROM
`member` AS M
WHERE M.name = #{nickname}
and delStatus = 0;

SELECT M.name AS
extra__writerName
FROM `member` AS M
WHERE M.id = #{id}
and delStatus = 0;
