mapper.select:
SELECT IFNULL(SUM(RP.point),0) AS '게시물 추천 여부'
FROM reactionPoint AS RP
WHERE RP.relTypeCode = #{relTypeCode}
AND RP.relId = #{relId}
AND RP.memberId = #{actorId}

mapper.delete:
DELETE FROM reactionPoint
WHERE memberId = #{actorId}
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND `point` = 1

DELETE FROM reactionPoint
WHERE memberId = #{actorId}
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND `point` = - 1

mapper.select:
SELECT IFNULL(SUM(RP.point),0) AS '댓글 추천 여부'
FROM reactionPoint AS RP
WHERE RP.relTypeCode = 'reply'
AND RP.relId = #{relId}
AND RP.memberId = #{actorId}

mapper.delete:
DELETE FROM reactionPoint
WHERE memberId = #{actorId}
AND relTypeCode = 'reply'
AND relId = #{relId}
AND `point` = - 1
