mapper.select:
SELECT RE.*, M.nickname AS extra__writerName FROM recipe AS RE LEFT JOIN `member` AS M ON RE.memberId = M.id WHERE 1 AND RE.recipeCategory = #{recipeCategory}
AND RE.recipeName LIKE CONCAT('%', #{searchKeyword}, '%')
AND RE.recipeStuff LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( RE.recipeName LIKE CONCAT('%', #{searchKeyword}, '%') OR RE.recipeStuff LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY RE.recipeId DESC LIMIT #{limitStart}, #{limitTake}

mapper.update:
UPDATE recipe
recipeCategory = #{recipeCategory}, recipeName = #{recipeName}, recipeCategory = #{recipeCategory}, recipeBody = #{recipeBody}, recipePerson = #{recipePerson}, recipeLevel = #{recipeLevel}, recipeCook = #{recipeCook}, recipeTime = #{recipeTime}, recipeStuff = #{recipeStuff}, recipeSauce = #{recipeSauce}, recipeMsgBody = #{recipeMsgBody}, recipeUpdateDate = NOW()
WHERE recipeId = #{recipeId}

UPDATE recipe
SET recipePoint = recipePoint + 1
WHERE recipeId = #{relId}

UPDATE recipe
SET recipePoint = recipePoint - 1
WHERE recipeId = #{relId}
