mapper.update:
UPDATE reply
SET updateDate = NOW(),
`body` = #{body}
WHERE id = #{id}

UPDATE reply
SET goodReactionPoint = goodReactionPoint + 1
WHERE id = #{relId}

UPDATE reply
SET goodReactionPoint = goodReactionPoint - 1
WHERE id = #{relId}

UPDATE reply
SET badReactionPoint = badReactionPoint + 1
WHERE id = #{relId}

UPDATE reply
SET badReactionPoint = badReactionPoint - 1
WHERE id = #{relId}
